    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        double capital = Double.parseDouble(jtfCapital.getText());
        double taxa = Double.parseDouble(jtftaxa.getText())/100;
        double mes = Double.parseDouble(jtfMes.getText());
        
        double montante = capital*(Math.pow(1+taxa,mes));
        int i = (int) Math.ceil(montante);
        
        lbResultado.setText("Montante de R$ "+i);
    }//GEN-LAST:event_jButton1ActionPerformed

